
/*global L */
"use strict";

var Game = require( 'GamePlanetside2' );

var ContinentLayer = L.LayerGroup.extend({
    initialize: function ( continent, game ) {
        var layer = L.tileLayer( '/maps/' + continent + '/{z}/{x}/{y}.jpg', {
            tms: true,
            noWrap: true
        });

        var layers = [ layer ];
        L.LayerGroup.prototype.initialize.call( this, layers );

        this.addLayer( game.getHexLayer( continent ) );
    }
});

module.exports = ContinentLayer;
