
/*global L */
"use strict";

var HexLayer = require('planetside2/HexLayer');

var Game = function () {
};

Game.prototype.getHexLayer = function( continent ) {
    return new HexLayer( continent );
};

Game.prototype.getContinents = function () { return [ "esamir", "indar", "amerish" ]; };

Game.prototype.getCRS = function () {
    var mapSize = 256;
    var projectionSize = 1;
    var projScale = mapSize;

    return L.extend({}, L.CRS, {
        projection: {
            project: function (latlng) {
                return new L.Point( latlng.lat, latlng.lng );
            },
            unproject: function (point) {
                return new L.LatLng( point.x, point.y );
            }
        },
        transformation: new L.Transformation(projScale,0,-projScale,256),

        scale: function (zoom) {
            return Math.pow( 2, zoom );
        }
    });
};

module.exports = new Game();
